<?php

namespace Entity;

/**
 * Class EntityConfig
 * This file is automatically generated only if it not exist. 
 */

class EntityConfig
{
    public static function getPDOLink(): \PDO
    {
        static $pdo = null;
        if (!$pdo) {
            try {
                $pdo = new \PDO(
                    'mysql:host=mysql;port=3306;dbname=thentity_test;charset=utf8mb4',
                    'root',
                    'root',
                    [
                        \PDO::ATTR_ERRMODE => \PDO::ERRMODE_EXCEPTION,
                        \PDO::ATTR_DEFAULT_FETCH_MODE => \PDO::FETCH_ASSOC
                    ]
                );
            } catch (\PDOException $e) {
                die('Erreur PDO: ' . $e->getMessage());
            }
        }
        return $pdo;
    }
}
